// src/App.js
import React, { useState } from "react";
import { WalletProvider, useWallet } from "@mysten/wallet-kit";
import { defineSlushWallet } from "@mysten/wallet-standard";
import "./App.css";

const slushWallet = defineSlushWallet({
  appName: "Trash Detector dApp",
});

function AppContent() {
  const { connect, connected, account } = useWallet();
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [result, setResult] = useState(null);

  const handleUpload = async () => {
    if (!file || !account) return;

    setUploading(true);
    setResult(null);

    const formData = new FormData();
    formData.append("video", file);
    formData.append("walletAddress", account.address);

    try {
      const res = await fetch("http://localhost:5000/api/upload", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setResult(data);
    } catch (err) {
      setResult({ error: "Eroare la upload" });
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Trash Detector dApp</h1>

        {!connected ? (
          <button onClick={connect} className="connect-btn">
            Conectează Slush Wallet
          </button>
        ) : (
          <div className="wallet-info">
            <p>
              Conectat: {account.address.slice(0, 6)}...
              {account.address.slice(-4)}
            </p>
          </div>
        )}

        {connected && (
          <>
            <input
              type="file"
              accept="video/*"
              onChange={(e) => setFile(e.target.files[0])}
              disabled={uploading}
            />
            <button
              onClick={handleUpload}
              disabled={!file || uploading}
              className="upload-btn"
            >
              {uploading ? "Procesez..." : "Upload & Verifică Gunoi"}
            </button>
          </>
        )}

        {result && (
          <div className={`result ${result.success ? "success" : "error"}`}>
            <p>{result.message || result.error}</p>
            {result.txHash && (
              <p>
                Tranzacție:{" "}
                <a
                  href={`https://suiscan.xyz/testnet/tx/${result.txHash}`}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  Vezi pe Suiscan
                </a>
              </p>
            )}
          </div>
        )}
      </header>
    </div>
  );
}

function App() {
  return (
    <WalletProvider defaultWallets={[slushWallet]} autoConnect>
      <AppContent />
    </WalletProvider>
  );
}

export default App;
