import React from "react";
import {
  WalletKitProvider,
  ConnectButton,
  useWalletKit,
} from "@mysten/wallet-kit";

// Componenta care arată wallet-ul curent și acțiuni
function WalletInfo() {
  const { currentWallet, disconnect } = useWalletKit();

  return (
    <div style={{ marginTop: "20px" }}>
      <ConnectButton /> {/* Butonul de conectare */}
      {currentWallet && (
        <div style={{ marginTop: "10px" }}>
          <p>
            <strong>Wallet conectat:</strong> {currentWallet.address}
          </p>
          <button onClick={() => disconnect()}>Deconectează Wallet</button>
        </div>
      )}
    </div>
  );
}

// Componenta principală App
function App() {
  return (
    <div style={{ padding: "20px" }}>
      <h1>Exemplu Sui + Slush Wallet</h1>
      <WalletInfo />
    </div>
  );
}

// Wrap App cu WalletKitProvider în index.tsx / index.jsx
export default function Root() {
  return (
    <WalletKitProvider
      autoConnect={true} // Se reconectează automat dacă wallet-ul este deja conectat
      chains={["sui:devnet"]} // Poți schimba în "sui:testnet" sau "sui:mainnet"
    >
      <App />
    </WalletKitProvider>
  );
}
