// src/App.js
import React, { useState } from "react";
import {
  WalletKitProvider,
  ConnectButton,
  useWalletKit,
} from "@mysten/wallet-kit";
import "./App.css";

// Componenta principală cu upload
function AppContent() {
  const { currentWallet } = useWalletKit();
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [result, setResult] = useState(null);

  const handleUpload = async () => {
    if (!file || !currentWallet) return;

    setUploading(true);
    setResult(null);

    const formData = new FormData();
    formData.append("video", file);
    formData.append("walletAddress", currentWallet.address);

    try {
      const res = await fetch("http://localhost:5000/api/upload", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setResult(data);
    } catch (err) {
      setResult({ error: "Eroare la upload" });
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Trash Detector dApp</h1>

        {/* Conectare Wallet */}
        <ConnectButton />

        {/* Info wallet */}
        {currentWallet && (
          <p style={{ margin: "10px 0" }}>
            Conectat: {currentWallet.address.slice(0, 6)}...
            {currentWallet.address.slice(-4)}
          </p>
        )}

        {/* Upload Video */}
        {currentWallet && (
          <>
            <input
              type="file"
              accept="video/*"
              onChange={(e) => setFile(e.target.files[0])}
              disabled={uploading}
              style={{ display: "block", margin: "10px 0" }}
            />
            <button
              onClick={handleUpload}
              disabled={!file || uploading}
              className="upload-btn"
            >
              {uploading ? "Procesez..." : "Upload & Verifică Gunoi"}
            </button>
          </>
        )}

        {/* Rezultat */}
        {result && (
          <div className={`result ${result.success ? "success" : "error"}`}>
            <p>{result.message || result.error}</p>
            {result.txHash && (
              <p>
                Tranzacție:{" "}
                <a
                  href={`https://suiscan.xyz/testnet/tx/${result.txHash}`}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  Vezi pe Suiscan
                </a>
              </p>
            )}
          </div>
        )}
      </header>
    </div>
  );
}

// Root App cu WalletKitProvider
export default function App() {
  return (
    <WalletKitProvider
      autoConnect={true}
      chains={["sui:testnet"]} // Testnet pentru hackathon
    >
      <AppContent />
    </WalletKitProvider>
  );
}
