import React from "react";
import { WalletKitProvider, useWalletKit } from "@mysten/wallet-kit";

// Example component to show wallet info
function WalletInfo() {
  const { currentWallet, connect, disconnect, signTransaction } =
    useWalletKit();

  return (
    <div>
      {currentWallet ? (
        <div>
          <p>Connected wallet: {currentWallet.address}</p>
          <button onClick={() => disconnect()}>Disconnect</button>
          <button
            onClick={() =>
              signTransaction({
                /* Your transaction details here */
              })
            }
          >
            Sign Transaction
          </button>
        </div>
      ) : (
        <button onClick={() => connect()}>Connect Wallet</button>
      )}
    </div>
  );
}

function App() {
  return (
    <WalletKitProvider
      // Optional: specify chains if needed, otherwise defaults will be used
      chains={["sui:devnet"]}
    >
      <div className="App">
        <h1>My Sui Wallet App</h1>
        <WalletInfo />
      </div>
    </WalletKitProvider>
  );
}

export default App;
