// src/lib/suiClient.js
import { getFullnodeUrl, SuiClient } from "@mysten/sui.js/client";
import { QueryClient } from "@tanstack/react-query";

// ==== AICI SCHIMBĂ DOAR UNA SINGURĂ DATĂ ====
// Pune exact rețeaua pe care o folosești în Slush Wallet
export const client = new SuiClient({
  url: getFullnodeUrl("testnet"), // ← dacă ai Slush pe Testnet
  // url: getFullnodeUrl("devnet"), // ← dacă ai Slush pe Devnet
});

export const queryClient = new QueryClient();
