import { Box, Typography, CircularProgress } from "@mui/material";
import { useWalletKit } from "@mysten/wallet-kit";
import { useQuery } from "@tanstack/react-query";

export default function Transactions() {
  const { currentWallet, isConnected } = useWalletKit();
  const address = currentWallet?.accounts?.[0]?.address;

  const {
    data: txs,
    isLoading,
    error,
  } = useQuery({
    queryKey: ["txs", address],
    queryFn: async () => {
      if (!address) return [];

      // RPC DIRECT – NU mai folosim client.getTransactionDigests deloc
      const res = await fetch("https://fullnode.testnet.sui.io:443", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          jsonrpc: "2.0",
          id: 1,
          method: "suix_queryTransactionDigests",
          params: [
            { FromAddress: address },
            null, // cursor = null
            15, // limit
            "descending", // order
          ],
        }),
      });

      const json = await res.json();
      if (json.error) throw new Error(json.error.message);
      return json.result?.data || [];
    },
    enabled: isConnected && !!address,
    retry: 3,
    refetchInterval: 15000,
  });

  if (!isConnected || !address)
    return <Typography>Conectează wallet-ul</Typography>;

  if (isLoading)
    return (
      <Box textAlign="center" py={6}>
        <CircularProgress />
        <Typography mt={2}>Se încarcă tranzacțiile...</Typography>
      </Box>
    );

  if (error)
    return <Typography color="error">Eroare: {error.message}</Typography>;

  return (
    <Box py={2}>
      <Typography variant="h5" gutterBottom>
        Ultimele tranzacții ({txs?.length || 0})
      </Typography>

      {txs?.length ? (
        <Box component="ul" sx={{ listStyle: "none", p: 0, m: 0 }}>
          {txs.map((tx) => (
            <Box
              component="li"
              key={tx.digest}
              sx={{ mb: 2, p: 2, bgcolor: "#f9f9f9", borderRadius: 2 }}
            >
              <a
                href={`https://suiscan.xyz/testnet/tx/${tx.digest}`}
                target="_blank"
                rel="noopener noreferrer"
                style={{
                  color: "#1976d2",
                  fontFamily: "monospace",
                  wordBreak: "break-all",
                }}
              >
                {tx.digest.slice(0, 10)}...{tx.digest.slice(-8)}
              </a>
            </Box>
          ))}
        </Box>
      ) : (
        <Typography color="text.secondary">
          Nicio tranzacție încă (cere SUI de pe faucet și revino)
        </Typography>
      )}
    </Box>
  );
}
