import { Box, Typography, CircularProgress } from "@mui/material";
import { useWalletKit } from "@mysten/wallet-kit";
import { useQuery } from "@tanstack/react-query";
import { client } from "../services/suiClient"; // sau calea ta corectă

export default function Transactions() {
  const { currentWallet, isConnected } = useWalletKit();

  // AICI e cheia: Slush Wallet v2 folosește accounts[0].address
  const address = currentWallet?.accounts?.[0]?.address;

  const {
    data: txs,
    isLoading,
    error,
  } = useQuery({
    queryKey: ["transactions", address],
    queryFn: async () => {
      if (!address) return [];

      // Funcționează perfect pe Testnet (nu mai e getTransactionDigests!)
      const resp = await client.getTransactions({
        filter: { FromAddress: address },
        order: "descending",
        limit: 15,
      });

      return resp.data || [];
    },
    enabled: isConnected && !!address,
    retry: 3,
    refetchInterval: 12000, // refresh la fiecare 12 secunde
  });

  if (!isConnected || !address) {
    return (
      <Typography>Conectează wallet-ul pentru a vedea tranzacțiile</Typography>
    );
  }

  if (isLoading) {
    return (
      <Box sx={{ textAlign: "center", py: 6 }}>
        <CircularProgress />
        <Typography sx={{ mt: 2 }}>Se încarcă tranzacțiile...</Typography>
      </Box>
    );
  }

  if (error) {
    return <Typography color="error">Eroare: {error.message}</Typography>;
  }

  return (
    <Box sx={{ py: 2 }}>
      <Typography variant="h5" gutterBottom>
        Ultimele tranzacții ({txs?.length || 0})
      </Typography>

      {txs && txs.length > 0 ? (
        <Box component="ul" sx={{ m: 0, pl: 3, listStyle: "none" }}>
          {txs.map((tx) => (
            <Box
              component="li"
              key={tx.digest}
              sx={{ mb: 2, p: 2, bgcolor: "#f5f5f5", borderRadius: 2 }}
            >
              <Typography variant="body2" color="text.secondary">
                Digest:
              </Typography>
              <a
                href={`https://suiscan.xyz/testnet/tx/${tx.digest}`}
                target="_blank"
                rel="noopener noreferrer"
                style={{
                  color: "#1976d2",
                  fontFamily: "monospace",
                  wordBreak: "break-all",
                }}
              >
                {tx.digest}
              </a>
            </Box>
          ))}
        </Box>
      ) : (
        <Typography color="text.secondary">
          Nicio tranzacție încă. Fă prima mișcare!
        </Typography>
      )}
    </Box>
  );
}
