import { Box, Typography, CircularProgress } from "@mui/material";
import { useWalletKit } from "@mysten/wallet-kit";
import { useQuery } from "@tanstack/react-query";

export default function Transactions() {
  const { currentWallet, isConnected } = useWalletKit();

  // Fix pentru Slush Wallet v2
  const address = currentWallet?.accounts?.[0]?.address;

  const {
    data: txs,
    isLoading,
    error,
  } = useQuery({
    queryKey: ["transactions", address],
    queryFn: async () => {
      if (!address) return [];

      // RPC DIRECT pe Testnet – funcționează 100 % în toate versiunile SDK
      const response = await fetch("https://fullnode.testnet.sui.io:443", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          jsonrpc: "2.0",
          id: 1,
          method: "suix_queryTransactionDigests",
          params: [
            {
              FromAddress: address,
            },
            {
              Order: "descending",
              Limit: 15,
            },
          ],
        }),
      });

      const result = await response.json();
      console.log("RPC Response:", result); // debug – șterge după

      if (result.error) {
        throw new Error(result.error.message);
      }

      return result.result?.data || [];
    },
    enabled: isConnected && !!address,
    retry: 3,
    refetchInterval: 12000, // refresh la fiecare 12 secunde
  });

  if (!isConnected || !address) {
    return (
      <Typography>Conectează wallet-ul pentru a vedea tranzacțiile</Typography>
    );
  }

  if (isLoading) {
    return (
      <Box sx={{ textAlign: "center", py: 6 }}>
        <CircularProgress />
        <Typography sx={{ mt: 2 }}>Se încarcă tranzacțiile...</Typography>
      </Box>
    );
  }

  if (error) {
    return <Typography color="error">Eroare: {error.message}</Typography>;
  }

  return (
    <Box sx={{ py: 2 }}>
      <Typography variant="h5" gutterBottom>
        Ultimele tranzacții ({txs?.length || 0})
      </Typography>

      {txs && txs.length > 0 ? (
        <Box component="ul" sx={{ m: 0, pl: 3, listStyle: "none" }}>
          {txs.map((tx) => (
            <Box
              component="li"
              key={tx.digest}
              sx={{ mb: 2, p: 2, bgcolor: "#f5f5f5", borderRadius: 2 }}
            >
              <Typography variant="body2" color="text.secondary">
                Digest:
              </Typography>
              <a
                href={`https://suiscan.xyz/testnet/tx/${tx.digest}`} // Testnet link
                target="_blank"
                rel="noopener noreferrer"
                style={{
                  color: "#1976d2",
                  fontFamily: "monospace",
                  wordBreak: "break-all",
                  display: "block",
                  mt: 1,
                }}
              >
                {tx.digest.slice(0, 8)}...{tx.digest.slice(-6)}
              </a>
            </Box>
          ))}
        </Box>
      ) : (
        <Typography color="text.secondary">
          Nicio tranzacție încă. Fă prima mișcare! (ex: cere SUI de pe faucet)
        </Typography>
      )}
    </Box>
  );
}
