import { Box, Typography, CircularProgress, Button } from "@mui/material";
import { useWalletKit } from "@mysten/wallet-kit";
import { useQuery } from "@tanstack/react-query";
import { client } from "../lib/suiClient"; // ← ACUM MERGE!

export default function Balance() {
  const { currentWallet } = useWalletKit();

  const { data, isLoading, error } = useQuery({
    queryKey: ["balance", currentWallet?.address],
    queryFn: async () => {
      const res = await client.getBalance({
        owner: currentWallet.address,
        coinType: "0x2::sui::SUI",
      });
      return Number(res.totalBalance) / 1_000_000_000;
    },
    enabled: !!currentWallet?.address,
  });

  if (!currentWallet) return <Typography>Conectează wallet-ul</Typography>;
  if (isLoading) return <Typography>Se încarcă soldul...</Typography>;
  if (error)
    return <Typography color="error">Eroare: {error.message}</Typography>;

  return (
    <Box sx={{ textAlign: "center", py: 8 }}>
      <Typography variant="h5" color="text.secondary" gutterBottom>
        Sold curent
      </Typography>
      <Typography variant="h1" color="primary" sx={{ fontWeight: "bold" }}>
        {data?.toFixed(6) ?? "0.000000"} SUI
      </Typography>

      {data < 0.1 && (
        <Button
          variant="contained"
          color="success"
          href="https://faucet.sui.io"
          target="_blank"
          sx={{ mt: 4 }}
        >
          Cere SUI gratuit
        </Button>
      )}
    </Box>
  );
}
