// src/App.js – Cod complet, funcțional pe desktop ȘI mobil (cu aplicații wallet)
import React, { useEffect, useState } from "react";
import {
  WalletKitProvider,
  ConnectButton,
  useWalletKit,
  SuiWallet,
  EthosWallet,
} from "@mysten/wallet-kit";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  useNavigate,
  useLocation,
} from "react-router-dom";
import { getFullnodeUrl, SuiClient } from "@mysten/sui.js/client";
import {
  Tabs,
  Tab,
  Box,
  AppBar,
  Toolbar,
  Typography,
  Container,
  Select,
  MenuItem,
  useMediaQuery,
  useTheme,
  Grid,
  Button as MuiButton,
} from "@mui/material";
import {
  QueryClient,
  QueryClientProvider,
  useQuery,
} from "@tanstack/react-query";

// ---------- Sui Client ----------
const client = new SuiClient({ url: getFullnodeUrl("devnet") });
const queryClient = new QueryClient();

// ---------- Protected Route ----------
function ProtectedRoute({ children }) {
  const { currentWallet } = useWalletKit();
  const navigate = useNavigate();

  useEffect(() => {
    if (!currentWallet) {
      navigate("/", { replace: true });
    }
  }, [currentWallet, navigate]);

  return currentWallet ? children : <Navigate to="/" replace />;
}

// ---------- Home Page (Connect) ----------
function HomePage() {
  const { currentWallet } = useWalletKit();
  const navigate = useNavigate();

  useEffect(() => {
    if (currentWallet) {
      navigate("/dashboard", { replace: true });
    }
  }, [currentWallet, navigate]);

  return (
    <Container maxWidth="sm" sx={{ textAlign: "center", mt: 10 }}>
      <Typography variant="h4" gutterBottom>
        Conectează-te cu wallet-ul Sui
      </Typography>

      {/* Buton principal WalletKit */}
      <ConnectButton />

      {/* Deep links pentru aplicații mobile */}
      <Box sx={{ mt: 4, display: "flex", flexDirection: "column", gap: 2 }}>
        <MuiButton
          variant="outlined"
          href="suiwallet://"
          size="large"
          sx={{ py: 1.5, fontWeight: 500 }}
        >
          Deschide Sui Wallet App
        </MuiButton>
        <MuiButton
          variant="outlined"
          href="ethos://"
          size="large"
          sx={{ py: 1.5, fontWeight: 500 }}
        >
          Deschide Ethos Wallet
        </MuiButton>
      </Box>
    </Container>
  );
}

// ---------- Tab Panel ----------
function TabPanel({ children, value, index }) {
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`tabpanel-${index}`}
      aria-labelledby={`tab-${index}`}
    >
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

// ---------- Dashboard with Responsive Tabs ----------
const TAB_PATHS = [
  "/dashboard/account",
  "/dashboard/transactions",
  "/dashboard/uploads",
  "/dashboard/balance",
  "/dashboard/nfts",
];
const TAB_LABELS = ["Cont", "Tranzacții", "Uploaduri", "Sold", "NFT"];

function Dashboard() {
  const location = useLocation();
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("md"));
  const [value, setValue] = useState(0);

  // Sync tab with URL
  useEffect(() => {
    const idx = TAB_PATHS.findIndex((path) =>
      location.pathname.startsWith(path)
    );
    if (idx !== -1) {
      setValue(idx);
    } else if (location.pathname === "/dashboard") {
      setValue(0);
      navigate("/dashboard/account", { replace: true });
    }
  }, [location.pathname, navigate]);

  const handleChange = (event, newValue) => {
    setValue(newValue);
    navigate(TAB_PATHS[newValue]);
  };

  const handleSelectChange = (event) => {
    const newValue = event.target.value;
    setValue(newValue);
    navigate(TAB_PATHS[newValue]);
  };

  return (
    <Box sx={{ width: "100%" }}>
      <AppBar position="static" color="primary">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Dashboard
          </Typography>
        </Toolbar>
      </AppBar>

      {/* Responsive: Tabs pe desktop, Select pe mobil */}
      <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
        {isMobile ? (
          <Select
            value={value}
            onChange={handleSelectChange}
            fullWidth
            sx={{ mx: 2, my: 1 }}
          >
            {TAB_LABELS.map((label, idx) => (
              <MenuItem key={idx} value={idx}>
                {label}
              </MenuItem>
            ))}
          </Select>
        ) : (
          <Tabs
            value={value}
            onChange={handleChange}
            variant="scrollable"
            scrollButtons="auto"
            sx={{ px: 2 }}
          >
            {TAB_LABELS.map((label, idx) => (
              <Tab key={idx} label={label} />
            ))}
          </Tabs>
        )}
      </Box>

      <TabPanel value={value} index={0}>
        <Account />
      </TabPanel>
      <TabPanel value={value} index={1}>
        <Transactions />
      </TabPanel>
      <TabPanel value={value} index={2}>
        <Uploads />
      </TabPanel>
      <TabPanel value={value} index={3}>
        <Balance />
      </TabPanel>
      <TabPanel value={value} index={4}>
        <NFTs />
      </TabPanel>
    </Box>
  );
}

// ---------- Account with Disconnect ----------
function Account() {
  const { currentWallet, disconnect } = useWalletKit();

  const handleDisconnect = () => {
    disconnect();
  };

  return (
    <>
      <Typography variant="h5" gutterBottom>
        Contul Meu
      </Typography>
      <Typography>
        <strong>Adresă:</strong>{" "}
        <Box
          component="span"
          sx={{ fontFamily: "monospace", fontSize: "0.9em" }}
        >
          {currentWallet?.address}
        </Box>
      </Typography>

      <Box sx={{ mt: 3 }}>
        <MuiButton
          variant="contained"
          color="error"
          onClick={handleDisconnect}
          size="large"
          sx={{ px: 4, py: 1.5 }}
        >
          Deconectează Wallet
        </MuiButton>
      </Box>
    </>
  );
}

// ---------- Transactions ----------
function Transactions() {
  const { currentWallet } = useWalletKit();

  const { data, isLoading, error } = useQuery({
    queryKey: ["txs", currentWallet?.address],
    queryFn: async () => {
      if (!currentWallet?.address) return [];
      const resp = await client.getTransactionDigests({
        filter: { FromAddress: currentWallet.address },
        order: "descending",
        limit: 10,
      });
      return resp.data || [];
    },
    enabled: !!currentWallet?.address,
  });

  if (isLoading) return <Typography>Încărcare tranzacții...</Typography>;
  if (error)
    return <Typography color="error">Eroare: {error.message}</Typography>;

  return (
    <>
      <Typography variant="h5" gutterBottom>
        Ultimele 10 tranzacții
      </Typography>
      {data && data.length > 0 ? (
        <Box component="ul" sx={{ pl: 2, m: 0 }}>
          {data.map((tx) => (
            <li key={tx.digest}>
              <a
                href={`https://suiscan.xyz/devnet/tx/${tx.digest}`}
                target="_blank"
                rel="noopener noreferrer"
                style={{ color: "#1976d2", textDecoration: "none" }}
              >
                {tx.digest.slice(0, 8)}...{tx.digest.slice(-6)}
              </a>
            </li>
          ))}
        </Box>
      ) : (
        <Typography color="text.secondary">
          Nu există tranzacții recente.
        </Typography>
      )}
    </>
  );
}

// ---------- Uploads ----------
function Uploads() {
  return (
    <>
      <Typography variant="h5" gutterBottom>
        Uploaduri
      </Typography>
      <Typography color="text.secondary">
        Aici poți adăuga un formular pentru upload fișiere (ex: IPFS pentru
        NFT-uri).
      </Typography>
    </>
  );
}

// ---------- Balance ----------
function Balance() {
  const { currentWallet } = useWalletKit();

  const { data, isLoading, error } = useQuery({
    queryKey: ["balance", currentWallet?.address],
    queryFn: async () => {
      if (!currentWallet?.address) return 0;
      const bal = await client.getBalance({ owner: currentWallet.address });
      return Number(bal.totalBalance) / 1_000_000_000;
    },
    enabled: !!currentWallet?.address,
  });

  if (isLoading) return <Typography>Încărcare sold...</Typography>;
  if (error)
    return <Typography color="error">Eroare: {error.message}</Typography>;

  return (
    <>
      <Typography variant="h5" gutterBottom>
        Sold curent
      </Typography>
      <Typography variant="h3" color="primary">
        {data?.toFixed(6)} SUI
      </Typography>
    </>
  );
}

// ---------- NFTs (Responsive Grid) ----------
function NFTs() {
  const { currentWallet } = useWalletKit();

  const { data, isLoading, error } = useQuery({
    queryKey: ["nfts", currentWallet?.address],
    queryFn: async () => {
      if (!currentWallet?.address) return [];
      const objs = await client.getOwnedObjects({
        owner: currentWallet.address,
        options: { showType: true, showDisplay: true, showContent: true },
      });
      return (
        objs.data.filter((o) => {
          const display = o.data?.display?.data;
          return display?.name || o.data?.type?.includes("nft");
        }) || []
      );
    },
    enabled: !!currentWallet?.address,
  });

  if (isLoading) return <Typography>Încărcare NFT-uri...</Typography>;
  if (error)
    return <Typography color="error">Eroare: {error.message}</Typography>;

  return (
    <>
      <Typography variant="h5" gutterBottom>
        NFT-urile tale
      </Typography>
      {data && data.length > 0 ? (
        <Grid container spacing={2} sx={{ px: 1 }}>
          {data.map((nft) => {
            const display = nft.data?.display?.data;
            const imageUrl =
              display?.image_url ||
              display?.url ||
              "https://via.placeholder.com/180";

            return (
              <Grid item xs={6} sm={4} md={3} key={nft.data?.objectId}>
                <Box
                  sx={{
                    border: "1px solid #ddd",
                    borderRadius: 2,
                    overflow: "hidden",
                    textAlign: "center",
                    bgcolor: "background.paper",
                  }}
                >
                  <img
                    src={imageUrl}
                    alt={display?.name || "NFT"}
                    style={{ width: "100%", height: 150, objectFit: "cover" }}
                  />
                  <Typography
                    variant="subtitle2"
                    sx={{ p: 1, fontWeight: "bold" }}
                  >
                    {display?.name || "Fără nume"}
                  </Typography>
                </Box>
              </Grid>
            );
          })}
        </Grid>
      ) : (
        <Typography color="text.secondary">Nu deții niciun NFT.</Typography>
      )}
    </>
  );
}

// ---------- Routing ----------
function AppRoutes() {
  return (
    <Routes>
      <Route path="/" element={<HomePage />} />
      <Route
        path="/dashboard/*"
        element={
          <ProtectedRoute>
            <Container maxWidth={false} sx={{ mt: 3, px: { xs: 1, sm: 3 } }}>
              <Dashboard />
            </Container>
          </ProtectedRoute>
        }
      />
      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
}

// ---------- Root Component ----------
export default function Root() {
  return (
    <QueryClientProvider client={queryClient}>
      <WalletKitProvider
        autoConnect={false}
        chains={["sui:devnet"]}
        enableMobileWallets={true}
        mobileWallets={[SuiWallet(), EthosWallet()]}
      >
        <Router>
          <AppRoutes />
        </Router>
      </WalletKitProvider>
    </QueryClientProvider>
  );
}
