import { useCurrentAccount, useSuiClientQuery } from "@mysten/dapp-kit";
import { Box, Typography, CircularProgress } from "@mui/material";

export default function Transactions() {
  const currentAccount = useCurrentAccount();
  const address = currentAccount?.address;

  const { data, isLoading, isError, error } = useSuiClientQuery(
    "queryTransactionBlocks",
    {
      filter: {
        FromAddress: address,
      },
      limit: 15,
      order: "descending",
      options: {
        showInput: false,
        showEffects: false,
        showEvents: false,
        showBalanceChanges: false,
      },
    },
    {
      enabled: !!address,
    }
  );

  if (!address) {
    return (
      <Typography>
        Conectează Slush Wallet pentru a vedea tranzacțiile.
      </Typography>
    );
  }

  if (isLoading)
    return (
      <Box sx={{ textAlign: "center", py: 6 }}>
        <CircularProgress />
        <Typography sx={{ mt: 2 }}>Încărcăm tranzacțiile...</Typography>
      </Box>
    );

  if (isError)
    return <Typography color="error">Eroare: {error?.message}</Typography>;

  const txs = data?.data || [];

  return (
    <Box sx={{ py: 2 }}>
      <Typography variant="h5" gutterBottom>
        Ultimele tranzacții ({txs.length})
      </Typography>

      {txs.length > 0 ? (
        <Box component="ul" sx={{ m: 0, p: 0, listStyle: "none" }}>
          {txs.map((tx) => (
            <Box
              component="li"
              key={tx.digest}
              sx={{ mb: 2, p: 2, bgcolor: "#f5f5f5", borderRadius: 2 }}
            >
              <Typography variant="body2" color="text.secondary">
                Digest:
              </Typography>

              <a
                href={`https://suiscan.xyz/testnet/tx/${tx.digest}`}
                target="_blank"
                rel="noopener noreferrer"
                style={{
                  color: "#1976d2",
                  display: "block",
                  fontFamily: "monospace",
                  wordBreak: "break-all",
                }}
              >
                {tx.digest}
              </a>
            </Box>
          ))}
        </Box>
      ) : (
        <Typography color="text.secondary">Nu există tranzacții.</Typography>
      )}
    </Box>
  );
}
